/*               General Functions              */
/* ============================================ */
document.getElementById("b-tb").onclick= function (){addToBL(document.getElementById("i-tb").value)};
document.getElementById("d-tb").onclick= function (){delFromBL()};
function delFromBL(){
  var chk=document.getElementsByName("tbch");
  for (let i = 0; i < chk.length; i++) {
    const element = chk[i];
    if(element.checked)element.parentElement.parentElement.remove(element);
  }
}
function dismBox() {document.getElementById("lrt").classList.toggle("show-lrt");}
function showBox(txt) {
  document.getElementById("lrt_body").innerHTML = txt;
  document.getElementById("lrt").classList.toggle("show-lrt");
}
document.getElementById("ok").addEventListener("click",dismBox);
function addToBL(val){
  if(val.length >2){
  var div=document.createElement("tr");
  div.innerHTML='<label class="chk"><input type="checkbox" name="tbch"><span class="checkmark"></span>'+val+'</label>';
  document.getElementById("tbtb").prepend(div);}
}

/* ============================================ */
/*               Save User Config               */
/* ============================================ */
function save_options() {
  var colors=[],actions=[],userActions=[],blist=[],splist=[];
  var ac = document.getElementById("av").querySelectorAll("div");
  var uc = document.getElementById("uv").querySelectorAll("div");
  var pos=document.getElementById("fpos").selectedIndex;
  var tbtb=document.getElementById("tbtb").rows;
  for (var i = 0; i < 5; i++) colors.push(getComputedStyle(document.body).getPropertyValue("--cl" + i));
  for (var i = 0; i < ac.length; i++)actions.push(ac[i].getAttribute('data-id'));
  for (var i = 0; i < uc.length; i++)userActions.push(uc[i].getAttribute('data-id'));
  for (let j = 0; j < 3; j++) splist.push(document.getElementById("sa-"+j).selectedIndex);
  for (let i = 0; i < tbtb.length; i++) blist.push(tbtb[i].firstChild.textContent);
  chrome.storage.local.set({
    colors: colors,
    actions: actions,
    userActions: userActions,
    fpos:pos,
    blist : blist,
    splist:splist
  }, function () {
    showBox('Options saved.<br>Remember to reload the page where the Floatly is used to update the style and actions'); 
  });
}
/* ============================================ */

/* ============================================ */
/*              Restore User Config             */
/* ============================================ */
function restore_options() {
  chrome.storage.local.get({
    colors: ["#0878A5", "#ffffff", "#0878A5", "#ffffff", "#101010"],
    actions: [0, 1, 2, 3, 4, 5, 6],
    userActions: [],
    fpos:0,
    blist:["github.com"],
    splist:[1,2,3]
  }, function (items) {var av = " ",uv = " ";
    var colors = items.colors, actions = items.actions,userActions = items.userActions,blacklist=items.blist,fpos=items.fpos,splist=items.splist;
    for (let j = 0; j < 3; j++) document.getElementById("sa-"+j).innerHTML=actionsString;
    for (var i = 0; i < 5; i++) document.body.style.setProperty("--cl"+i, colors[i]);
    for (var i = 0; i < actions.length; i++)av += actionsDiv[actions[i]];
    for (var i = 0; i < userActions.length; i++)uv += actionsDiv[userActions[i]];
    var diff=userActions.length + actions.length;
    if(diff<actionsDiv.length){
      newA=1;
      for(var i=diff;i<actionsDiv.length;i++)av += actionsDiv[i];
    }
    document.getElementById("av").innerHTML = av;
    document.getElementById("uv").innerHTML = uv;
    document.getElementById("fpos").selectedIndex=fpos;
    for (let i = 0; i < blacklist.length; i++)  addToBL(blacklist[i]);
    for (let j = 0; j < splist.length; j++) document.getElementById("sa-"+j).selectedIndex=splist[j];
  });

/* ============================================ */
/*                Sortable Config               */
/* ============================================ */
  new Sortable(document.getElementById("av"), {
    group: 'shared',
    animation: 150,
	  ghostClass: "av_ghost",
	  chosenClass: "av_chosen",
    sort:false,
    onChoose: function (evt) {      
      document.getElementById("sv").innerHTML=actionsString[evt.item.dataset.id];
    }
  });
  new Sortable(document.getElementById("uv"), {
    group: 'shared',
    animation: 150,
    onChoose: function (evt) {      
      document.getElementById("sv").innerHTML=actionsString[evt.item.dataset.id];
    }
  });
  /* ============================================ */
}


